import React from 'react';

export const theme = {
    light: {
        color: '#212529',
        backgroundColor: '#f8f9fa',
        borderColor: '#f8f9fa',
    },
    dark: {
        color: '#fff',
        backgroundColor: '#343a40',
        borderColor: '#343a40',
    },
};

export const ThemeContext = React.createContext({
    theme: themes.light,
    toggleTheme: () => {},
});
